import { queryDB, getCategories } from '../../../../db';
import ArticleDisplay from '../../../../components/ArticleDisplay';
import Layout from '../../../../layouts';
import { convertToPath, convertFromPath } from '../../../../Functions';

export async function getStaticPaths() {
    let categories = await queryDB("SELECT title, articles FROM categories WHERE title <> 'Labyrinth'"),
        paths = categories.map(({title, articles}) =>
            articles.map((article, i) =>
                ({ params: { category: convertToPath(title), page: String(i+1) } })
            )
        );
    paths = paths.reduce((acc, cur) => [...cur, ...acc], []);

    return { paths, fallback: false }
}

export async function getStaticProps({ params: { category, page } }) {
    let articleIDs = await queryDB('SELECT articles FROM categories WHERE title = $1', [convertFromPath(category)]),
        articles = await queryDB('SELECT * FROM articles WHERE id = ANY($1)', [articleIDs[0].articles]);

    return {
        props: JSON.parse(JSON.stringify({
            heading: convertFromPath(category),
            articles,
            categories: await getCategories(),
            page: Number(page)
        }))
    }
}

export default ({ heading, articles, categories, page }) => <Layout categories={categories}> <ArticleDisplay type="category" heading={heading} articles={articles} page={page} /> </Layout>
