function Comment({ comment: { picture, name, email, content, post_date } }, topLevel, moreBelow) {
    return (<>
        <div className="comment">
            <img src={picture || 'https://secure.gravatar.com/avatar/004ce453a2b46792c3e04146555a35b3?s=64&d=mm&r=g'} alt="user" />
            <div>
                <div className="row">
                    <span>{name}</span>
                    <i className="fas fa-link"></i>
                </div>
                <div>{content}</div>
                <div className="row">
                    <button>{topLevel ? <i className="far fa-comments" ></i> : ''} Reply</button>
                    <span>
                        <span style={{ color: '#999', fontSize: '.75rem' }}>
                            <i className="far fa-clock" style={{ marginRight: '.325rem' }}></i>
                            {post_date}
                        </span>
                        {moreBelow ? <i className="fas fa-chevron-up"></i> : ''}
                    </span>
                </div>
            </div>
        </div>

        <style jsx>{`
            .comment {
                display: flex;
            }

            img {
                object-fit: fill;
                height: 5rem;
                width: 5rem;
                box-shadow: 0 0 2px 2px #d8d8d8;
                margin-right: 1rem;
            }

            i {
                color: #999;
            }

            .row {
                display: flex;
                justify-content: space-between;
            }

            .comment > div {
                background-color: #f7f7f7;
                padding: .5rem;
                margin-left: .5rem;
                font-size: .9rem;
                width: 100%;
            }

            .comment > div > div:nth-child(1) span {
                color: #00B38F;
                font-size: 1.1rem;
            }

            .comment > div >div:nth-child(2) {
                margin-top: 2rem;
                margin-bottom: 1rem;
            }

            button {
                margin-left: .75rem;
                color: #00B38F;
                border: 1px solid #00B38F;
                padding: .3rem .5rem;
                font-size: .75rem;
                cursor: pointer;
            }

            button i {
                color: #00B38F;
                margin-right: .15rem;
            }

            button:hover,
            button:hover i {
                color: white;
                background-color: #00B38F;
            }
        `}</style>
    </>)
}

export default Comment
