import { convertDate } from "../Functions"

function Comment({ comment: { picture, name, email, content, post_date, id, parent, replies } }) {
    return (<>
        <div className="comment">
            <span style={{ marginRight: '1rem', fontSize: '.775rem' }}>
                <img src={picture || 'https://secure.gravatar.com/avatar/004ce453a2b46792c3e04146555a35b3?s=64&d=mm&r=g'} alt="user" />
                <div style={{ color: '#00B38F', textAlign: 'center' }}>{name}</div>
            </span>
            <div>
                <div className="row">
                    <span>{name}</span>
                    <span><i className="fas fa-link"></i></span>
                </div>
                <div>{content}</div>
                <div className="row">
                    <button>{!parent ? <i className="far fa-comments" ></i> : ''} Reply</button>
                    <span>
                        <span style={{ color: '#999', fontSize: '.75rem' }}>
                            <i className="far fa-clock" style={{ marginRight: '.325rem' }}></i>
                            {convertDate(post_date)}
                            {replies.length ? <i style={{ color: '#00B37F', marginLeft: '.325rem' }} className="fas fa-chevron-up"></i> : ''}
                        </span>
                    </span>
                </div>
                <form>
                <div style={{ position: 'relative' }}>
                    <img className="input-large-img" src="https://secure.gravatar.com/avatar/?s=40&d=mm&r=g" alt="user" />
                    <input type="text" placeholder="Join the discussion..." />
                </div>
                <div style={{ position: 'relative' }}>
                    <i className="fas fa-user input-icon"></i>
                    <input className="input-small" type="text" placeholder="Name*" />
                </div>
                <div style={{ position: 'relative' }}>
                    <i className="fas fa-at input-icon"></i>
                    <input className="input-small" type="text" placeholder="Email*" />
                </div>
                <button className="post-comment-button">Post Comment</button>
                </form>
            </div>
        </div>

        <style jsx>{`
            .comment {
                display: flex;
            }

            img {
                object-fit: fill;
                height: 5rem;
                width: 5rem;
                box-shadow: 0 0 2px 2px #d8d8d8;
                margin-bottom: .5rem;
            }

            i {
                color: #999;
            }

            .row {
                display: flex;
                justify-content: space-between;
            }

            .comment > div {
                ${parent ? 'background-color: #f7f7f7;' : ''}
                padding: .75rem;
                margin-left: .5rem;
                font-size: .85rem;
                width: 100%;
            }

            .comment > div > div:nth-child(1) span {
                color: #00B38F;
                font-size: 1.1rem;
            }

            .comment > div >div:nth-child(2) {
                margin-top: 2rem;
                margin-bottom: 1rem;
            }

            button {
                margin-left: .75rem;
                color: #00B38F;
                border: 1px solid #00B38F;
                padding: .3rem .55rem;
                font-size: .75rem;
                cursor: pointer;
            }

            button i {
                color: #00B38F;
                margin-right: .175rem;
            }

            button:hover,
            button:hover i {
                color: white;
                background-color: #00B38F;
            }

            .comment > div > div:nth-child(1) > span:nth-child(2) {
                display: flex;
                justify-content: center;
                align-items: center;
                width: 1.5rem;
                height: 1.5rem;
                cursor: pointer;
                font-size: .9rem;
            }

            .comment > div > div:nth-child(1) > span:nth-child(2):hover,
            .comment > div > div:nth-child(1) > span:nth-child(2):hover i {
                background-color: #00B38F;
                color: white;
            }

            .post-comment-button {
                background-color: #666;
                color: white;
                padding: .5rem 1.25rem;
            }
        `}</style>
    </>)
}

export default Comment
