import Link from 'next/link';
import { useState } from 'react';

function Nav({ categories }) {   
    const [timer, setTimer] = useState(null);
    
    return (<>
        <ul className="nav">
            {categories.sort((a, b) => ([a.title, b.title].sort()[0] === a.title) ? 0 : 1).map((category, i) =>
                <li key={i} onMouseOut={e => {
                    let target = e.currentTarget
                    setTimer(setTimeout(_=> {
                    target.children[1].style.opacity = 0;
                    target.children[1].style.pointerEvents = 'none';
                    }, 200))}}>
                    <Link href={`/categories/${category.title.toLowerCase().replace(/ /g, '-')}`}><a onMouseOver={e => {
                        timer && clearTimeout(timer)
                        e.target.parentElement.children[1].style.opacity = 1;
                        e.target.parentElement.children[1].style.pointerEvents = 'auto';
                    }}>{category.title}</a></Link>
                    <ul className="nav-subcategories">
                        {Object.keys(category.subcategories).map((subcategory, i) =>
                            <li key={i} onMouseOut={e => {
                                let target = e.currentTarget
                                setTimer(setTimeout(_=> {
                                target.children[1].style.opacity = 0;
                                target.children[1].style.pointerEvents = 'none';
                            }, 200))}} onMouseOver={e => {
                                timer && clearTimeout(timer);
                                e.target.parentElement.children[1].style.opacity = 1;
                                e.target.parentElement.children[1].style.pointerEvents = 'auto';
                            }}>{subcategory}</li>)}
                    </ul>
                </li>)
            }
            <li><icon src="fa-search" /></li>
        </ul>

        <style jsx>{`
            .nav {
                display: flex;
                justify-content: space-evenly;
                align-items: center;
                height: 3rem;
                text-transform: uppercase;
                font-weight: 600;
            }

            .nav li {
                position: relative;
            }

            a {
                color: black;
            }

            a:hover {
                color: var(--link-hover);
            }

            .nav-subcategories {
                padding: .5rem .6rem;
                font-weight: 300;
                background-color: #fff;
                opacity: 0;
                pointer-events: none;
                position: absolute;
                top: 175%;
                transition: opacity 250ms ease-in;
                font-size: 15px;
            }

            .nav-subcategories li {
                margin-top: 7px;
            }

            .nav-subcategories li:nth-child(1) {
                margin-top: 0;
            }

            .nav-subcategories li:hover {
                color: var(--link-hover);
            }
        `}</style>
    </>)
}

export default Nav
