import Link from 'next/link';

function CategoryPreview({ category: { title, articles } }) {
    return (
        <div className="category-preview">
            <Link href={`/categories/${title.toLowerCase().replace(/ /g, '-')}`}><label>{title}</label></Link>
            <ul>
                {/* three most recent articles in category, sort by publish_date*/}
                {articles.slice(0, 3).map((article, i) =>
                    <li key={i}>
                        <div style={{ fontWeight: 600, fontSize: '1.2rem' }}><Link href={`/articles/${article.id}`}><a className="article-preview-title">{article.title}</a></Link></div>
                        <div className="date-and-author">{new Date(article.publish_date).toLocaleDateString('default', { month: 'long', year: 'numeric', day: 'numeric' })} by <Link href={`/authors/${article.author.id}`}><a className="article-preview-author">{article.author.name}</a></Link></div>
                        <div style={{ display: 'flex' }}>
                            <img src={article.full_thumbnail} alt="thumbnail" />
                            <div style={{ display: 'flex', flexDirection: 'column', marginLeft: '.4rem', fontSize: '.9rem' }}>
                                <span>{article.description}</span>
                                <Link href={`/articles/${article.id}`}><a className="read-more">read more</a></Link>
                            </div>
                        </div>
                    </li>
                )}
            </ul>
            <style jsx>{`
                ul {
                    display: flex;
                    flex-direction: column;
                    position: relative;
                }

                li::after {
                    content: '';
                    position: absolute;
                    height: 100%;
                    width: 2px;
                    background-color: #e0e0e0;
                    right: 0;
                }

                li {
                    margin: 1rem;
                    position: relative;
                }

                li:nth-child(1)::after,
                li:nth-child(2)::after {
                    position: absolute;
                    content: '';
                    height: 2px;
                    width: 100%;
                    bottom: -1.2rem;
                    background-color: #e0e0e0;
                }

                label {
                    margin-left: 1rem;
                    position: relative;
                    font-size: 2rem;
                    padding: 4px 0;
                    display: block;
                    font-weight: 600;
                }

                label::before,
                label::after {
                    position: absolute;
                    content: '';
                    height: 2px;
                    width: 100%;
                    background-color: var(--theme-gold);
                    left: -.65rem;
                }

                label::before {
                    top: 0;
                }

                label::after {
                    bottom: 0;
                }

                .article-preview-title {
                    color: black;
                }

                .article-preview-author {
                    color: gray;
                    font-style: italic;
                }

                img {
                    height: 6.25rem;
                    width: 7.75rem;
                    object-fit: cover;
                }

                a:hover {
                    color: var(--link-hover);
                }
            `}</style>
        </div>
    )
}

export default CategoryPreview
