import Link from 'next/link';
import { convertDate } from '../Functions';

function Related({ articles }) {
    return (<>
        <div className="related">
            <div>Related</div>
            <ul>
                {articles.map((article, i) =>
                    <li key={i}>
                        <Link href={`/articles/${article.id}`}><a>{article.title}</a></Link>
                        <div>{convertDate(article.publish_date)}</div>
                        <div>In "{convertDate(article.category)}"</div>
                    </li>
                )}
            </ul>
        </div>

        <style jsx>{`
            .related {
                margin: 3rem 0;
            }
            .related > div:nth-child(1) {
                position: relative;
                margin-bottom: .8rem;
                font-size: .8rem;
                font-weight: 600;
            }

            .related > div:nth-child(1)::before {
                content: '';
                background-color: gray;
                height: 1px;
                width: 100%;
                position: absolute;
                top: -100%;
            }

            ul {
                display: flex;
            }

            li {
                display: flex;
                flex-direction: column;
                width: 25%;
                margin-right: 1.65rem;
            }

            li > div {
                color: gray;
                font-family: Georgia, serif;
                margin-bottom: 0 .15rem;
                font-size: .9rem;
            }

            a {
                color: var(--link-hover);
                font-family: Georgia, serif;
                margin-bottom: .25rem;
            }

            a:hover {
                text-decoration: underline;
            }
        `}</style>
    </>)
}

export default Related
