import { queryDB } from '../../Functions';
import ArticleDisplay from '../../components/ArticleDisplay';

export async function getStaticPaths() {
    let titles = await queryDB("SELECT title FROM categories WHERE title <> 'Labyrinth'");
    let paths = titles.map(title => ({ params: { category: title.title.toLowerCase().replace(/ /g, '-') } }));

    return { paths, fallback: false }
}

export async function getStaticProps({ params }) {
    let articleIDs = await queryDB('SELECT articles FROM categories WHERE title = $1', [params.category]),
        articles = await queryDB('SELECT * FROM articles WHERE id = ANY($1)', [articleIDs.articles]);

    return {
        props: JSON.parse(JSON.stringify({
            heading: params.category,
            articles
        }))
    }
}

export default ({ heading, articles }) => <ArticleDisplay type="category" heading={heading} articles={articles} />
