import Header from '../components/Header';
import Sidebar from '../components/Sidebar';

function Layout({ categories, children }) {
    return (<>
        <div style={{ backgroundColor: '#444', color: '#fff', height: '8rem', display: 'flex', justifyContent: 'center', alignItems: 'center' }}><span>Ad Space</span></div>
        <div className="main">
            <Header categories={categories} />
            <div style={{ display: 'flex' }}>
                {children}
                <Sidebar />
            </div>
        </div>
        <div style={{ display: 'flex', justifyContent: 'center' }}>
            <div style={{ backgroundColor: '#444', color: '#fff', height: '7rem', display: 'flex', justifyContent: 'center', alignItems: 'center', position: 'fixed', bottom: 0, width: '50rem', zIndex: 3 }}>
                <span>Ad Space</span>
                <button className="bottom-ad-button">x</button>
            </div>
        </div>

        <style jsx>{`
           .main {
               margin: 0 1rem 1rem 1rem;
           }

           .bottom-ad-button {
               background-color: tomato;
               color: white;
               font-size: .75rem;
               padding: 0 .25rem;
               position: absolute;
               top: 4px;
               right: 4px;
           }
        `}</style>
    </>)
}

export default Layout
