import Nav from './Nav';
import Link from 'next/link';
import CSSVars from '../css/variables';

function Header({ categories }) {
    return (<>
        <div className="header">
            <div>Ad Space</div>
            <img src="nexus-logo.png" alt="logo" />
            <div className="under-logo">
                <span>{(_=> {
                    let date = new Date().toLocaleDateString('default', { month: 'long' });
                    return date;
                    return `${date.getDay()}, ${date.getMonth()}, ${date.getFullYear()}`;
                })()}</span>
                <div>UNIVERSITY OF CALIFORNIA, SANTA BARBARA</div>
                <span>
                    asdf
                    <Link href="facebook.com/dailynexus"><icon src="fa-facebook" /></Link>
                    <Link href="twitter.com/dailynexus"><icon src="fa-twitter" /></Link>
                    <Link href="instagram.com/dailynexus"><icon src="fa-instagram" /></Link>
                </span>
            </div>
            <Nav categories={categories} />
            <Link href="/articles/1843"><a>
                <span>{'LIVE: Updates on Coronavirus in Santa Barbara County, on UCSB Operations' /* manage in DB */ }</span>
                <span style={{ fontSize: '1.5rem' }}>click to read more</span>
            </a></Link>
        </div>
        <style jsx>{`
            a {
                display: flex;
                flex-direction: column;
                padding: .75rem;
                font-size: 2rem;
                color: white;
                background-color: ${CSSVars.theme.blue};
                text-align: center;
            }

            .under-logo {
                position: relative;
                text-align: center;
                font-size: .825rem;
                padding: 4px;
            }

            .under-logo span {
                position: absolute;
            }

            .under-logo span:nth-child(1) {
                left: 0;
            }

            .under-logo span:nth-child(3) {
                right: 0;
            }

            .under-logo::before,
            .under-logo::after {
                width: 100%;
                content: '';
                position: absolute;
                left: 0;
            }

            .under-logo::before {
                background-color: ${CSSVars.theme.blue};
                height: 1px;
                top: 0;
            }

            .under-logo::after {
                height: 3px;
                bottom: 0;
                background-color: ${CSSVars.theme.gold}; 
            }
        `}</style>
    </>)
}

export default Header
