import Link from 'next/link';
import { convertDate } from '../Functions';

function ArticlePreview({ article, imageLeft }) {
    return (<>
        <div className="article-preview">
            {imageLeft && <img src={article.full_thumbnail} alt="thumbnail" />}
            <div className="article-preview-words">
                <div><Link href={`/articles/${article.id}`}><a className="article-preview-title">{article.title}</a></Link></div>
                <div className="date-and-author">{convertDate(article.publish_date)} by <Link href={`/author/${article.author.id}`}><a className="article-preview-author">{article.author.name}</a></Link></div>
                <div>{article.description}</div>
                <Link href={`/articles/${article.id}`}><a className="read-more">read more</a></Link>
            </div>
            {!imageLeft && <img src={article.full_thumbnail} alt="thumbnail" />}
        </div>

        <style jsx>{`
            .article-preview {
                display: flex;
                margin: 2.5rem 1rem;
            }

            .article-preview-words {
                display: flex;
                flex-direction: column;
                margin: 0 .75rem;
                width: 80%;
            }

            .article-preview-title {
                color: black;
                font-size: 1.75rem;
                font-weight: 600;
            }

            .article-preview-author {
                color: gray;
                font-style: italic;
            }

            a:hover {
                color: var(--link-hover);
            }
        `}</style>
    </>)
}

export default ArticlePreview
