import Link from 'next/link';

function About({ header, children }) {
    return (
        <div style={{ margin: '1rem .5rem 0 1rem' }}>
            <div style={{ color: 'var(--link-hover)', fontSize: '2rem', marginBottom: '1.5rem', fontWeight: 'bold' }}>{header}</div>
            {children}
        </div>
    )
}

export default About
