import { queryDB } from '../../db';

export default async function(req, res) {
    let { id, email, article_id } = req.body;
    try {
        let [articleComments] = await queryDB('SELECT comments FROM articles WHERE id = $1', [article_id]);
        let [userComments] = await queryDB('SELECT comments FROM users WHERE email = $1', [email]);
        function deleteComment(id, level) {
            return level.forEach(comment => {
                if (comment.id === id) {
                    comment.content = comment.author = 'deleted';
                } else deleteComment(id, comment.replies);
            });
        }
        console.log('article', JSON.stringify(articleComments.comments));
        console.log('user', JSON.stringify(userComments.comments));
        deleteComment(id, articleComments.comments);
        deleteComment(id, userComments);
        await queryDB('UPDATE articles SET comments = $1 WHERE id = $2', [articleComments.comments, article_id]);
        await queryDB('UPDATE users SET comments = $1 WHERE email = $2', [userComments.comments, email]);
        res.json(0);
        res.end();
    } catch (e) { console.log(e) }
}
