import 'isomorphic-unfetch';
import { useState } from 'react'

function CommentSection({ comments, articleID }) {
    const [commentMessage, setCommentMessage] = useState(null);
    
    return (<>
        <div className="comment-section">
            <div>
                <span>{comments.length}</span>
                <span>Leave a Reply</span>
            </div>
            <strong style={{ color: 'red', fontSize: '1.15rem' }}>{commentMessage}</strong>
            <form style={{ position: 'relative', marginBottom: '2.35rem', marginTop: '1rem' }} onSubmit={async e => {
                e.preventDefault();
                const [content, name, email] = [0, 1, 2].map(i => e.target.children[i].children[1].value);
                if (!content || !name || !email) { return setCommentMessage('There is an empty field') }
                let response = await fetch('../api/post-comment', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                        articleID,
                        name,
                        email,
                        content
                    })
                });

                let status = await response.text(),
                    msg;
                switch (status) {
                    case 1:
                        msg = 'Invalid email address';
                        break;
                    case 2:
                        msg = 'This email is registered with a different name';
                        break;
                    case 3:
                        msg = 'New user created';
                        break;
                    default:
                        console.log(status);
                }
                msg && setCommentMessage(msg);
                !status && e.target.reset();
            }}>
                <div style={{ position: 'relative' }}>
                    <img className="input-large-img" src="https://secure.gravatar.com/avatar/?s=40&d=mm&r=g" alt="user" />
                    <input type="text" placeholder="Join the discussion..." />
                </div>
                <div style={{ position: 'relative' }}>
                    <i className="fas fa-user input-icon"></i>
                    <input className="input-small" type="text" placeholder="Name*" />
                </div>
                <div style={{ position: 'relative' }}>
                    <i className="fas fa-at input-icon"></i>
                    <input className="input-small" type="text" placeholder="Email*" />
                </div>
                <button>Submit</button>
            </form>
            <ul className="icons">
                <li key="0"><i className="fas fa-align-left" ></i></li>
                <li key="1"><i className="far fa-comments" ></i></li>
                <li key="2"><i className="fas fa-rss" ></i></li>
                <li key="3"><i className="fas fa-bolt" style={{ cursor: 'pointer' }}></i></li>
                <li key="4"><i className="fab fa-hotjar" style={{ cursor: 'pointer' }}></i></li>
            </ul>
            <ul className="comments">
                {comments.map((comment, i) => <li key={i}><Comment comment={comment} /></li>)}
            </ul>
        </div>

        <style jsx>{`
            .comment-section > div:nth-child(1) {
                position: relative;
                display: flex;
                margin-bottom: 4rem;
            }

            .comment-section > div:nth-child(1)::after {
                position: absolute;
                content: '';
                background-color: lightgray;
                width: 100%;
                bottom: -1.25rem;
                height: 1px;
                left: 0;
            }

            .comment-section > div:nth-child(1) > span {
                margin-right: .9rem;
                font-size: 1.3rem;
            }

            .comment-section > div:nth-child(1) > span:nth-child(1) {
                background-color: #666;
                padding: .325rem 1rem;
                font-size: 1rem;
                color: white;
            }

            input {
                padding: 1.7rem;
                padding-left: 5rem;
                border: 1px solid #ddd;
                color: #888;
                font-family: monospace;
                font-size: 1.15rem;
                width: 100%;
                margin-bottom: .6rem;
            }

            .input-large-img {
                position: absolute;
                top: 1rem;
                left: 1rem;
                box-shadow: 0 0 4px 2px #d8d8d8;
            }

            .input-small {
                font-size: .9rem;
                padding: .5rem 1.9rem;
                width: 50%;
                font-family: Lato;
            }

            li {
                margin: .5rem;
            }

            .input-icon {
                color: #aaa;
                position: absolute;
                top: .65rem;
                left: .5rem;
            }

            .comment-section > div:nth-child(2)::after {
                content: '';
                background-color: lightgray;
                width: 100%;
                height: 1px;
                position: absolute;
                bottom: -.95rem;
            }

            .icons {
                display: flex;
                position: relative;
                width: 9.5rem;
            }

            .icons li {
                margin: 0 .75rem;
                color: #666;
                font-size: 1.05rem;
            }

            .icons li:nth-child(1) i {
                transform: rotateZ(3.15rad);
            }
            
            .icons li::before {
                border-radius: 8px;
                background-color: #555;
                text-align: center;
                font-size: .75rem;
                padding: .65rem 0;
                width: 127%;
                left: 2%;
                top: -240%;
                color: white;
                position: absolute;
                display: none;
            }

            .icons li:hover::before {
                display: block;
            }

            .icons li:nth-child(4) i { color: #ffba20; }
            .icons li:nth-child(4) i:hover { color: #ffa600; }

            .icons li:nth-child(5) i { color: #ff785b; }
            .icons li:nth-child(5) i:hover { color: #fc5844; }

            .icons li:nth-child(1)::before { content: 'Comment threads'; }
            .icons li:nth-child(2)::before { content: 'Thread replies'; }
            .icons li:nth-child(3)::before { content: 'Followers'; }
            .icons li:nth-child(4)::before { content: 'Most reacted comment'; }
            .icons li:nth-child(5)::before { content: 'Hottest comment thread'; }

            .comments {
                position: relative;
                margin-top: 3rem;
            }

            .comments::before {
                background-color: #f7f7f7;
                height: 1.25rem;
                width: 100%;
                content: '';
                position: absolute;
                top: -2rem;
            }
        `}</style>
    </>)
}

export default CommentSection
