import { config } from 'dotenv';
import { Pool } from 'pg';
config();

if (!pool) {
    var pool = new Pool({
        connectionString: process.env.DATABASE_URL,
        idleTimeoutMillis: 0
    });
    pool
        ? console.log('Connected to PostgreSQL')
        : console.log('Failed to connect to database');
}

export default pool;
