import Link from 'next/link';
import CSSVars from '../css/variables';

function Nav({ categories }) {    
    return (<>
        <ul>
            {categories.map((category, i) =>
                <li key={i}>
                    <Link href={`/categories/${category.title}`}><a>{category.title}</a></Link>
                </li>)
            }
            <li><icon src="fa-search" /></li>
        </ul>

        <style jsx>{`
            ul {
                display: flex;
                justify-content: space-evenly;
                align-items: center;
                height: 3rem;
                text-transform: uppercase;
                font-weight: 600;
            }

            a {
                color: black;
            }

            a:hover {
                color: ${CSSVars.hover.link};
            }
        `}</style>
    </>)
}

export default Nav
