export default ({ comments }) => (<>
    <div className="comment-section">
        <div>
            <span>{comments.length}</span>
            <span>Leave a Reply</span>
        </div>
        <div>
            <input className="input-large" type="text" placeholder="Join the discussion..." />
            <div style={{ position: 'relative' }}>
                <i className="fas fa-user"></i>
                <input className="input-small" type="text" placeholder="Name*" />
            </div>
            <div style={{ position: 'relative' }}><i className="fas fa-at"></i>
                <input className="input-small" type="text" placeholder="Email*" />
            </div>
        </div>
        <ul style={{ display: 'flex' }}>
            <li key="0"><i className="fas fa-align-left"></i></li>
            <li key="1"><i className="far fa-comments"></i></li>
            <li key="2"><i className="fas fa-rss"></i></li>
            <li key="3"><i className="fas fa-bolt"></i></li>
            <li key="4"><i className="fab fa-hotjar"></i></li>
        </ul>
        <ul className="comments">
            {comments.map((comment, i) => <li key={i}><Comment comment={comment} /></li>)}
        </ul>
    </div>

    <style jsx>{`
        .comment-section > div:nth-child(1) {
            position: relative;
            display: flex;
            margin-bottom: 4rem;
        }

        .comment-section > div:nth-child(1)::after {
            position: absolute;
            content: '';
            background-color: lightgray;
            width: 100%;
            bottom: -1.25rem;
            height: 1px;
            left: 0;
        }

        .comment-section > div:nth-child(1) > span {
            margin-right: .9rem;
            font-size: 1.3rem;
        }

        .comment-section > div:nth-child(1) > span:nth-child(1) {
            background-color: #666;
            padding: .325rem 1rem;
            font-size: 1rem;
            color: white;
        }

        input {
            padding: 1.5rem;
            padding-left: 5rem;
            border: 1px solid #ddd;
            color: #888;
            font-family: monospace;
            font-size: 1.15rem;
            width: 100%;
            margin-bottom: .6rem;
        }

        input::before {
            content: '';
            background-image: url('https://secure.gravatar.com/avatar/?s=40&d=mm&r=g');
            position: absolute;
            left: 1rem;
            top: 1rem;
        }

        .input-small {
            font-size: .9rem;
            padding: .5rem 1.9rem;
            width: 50%;
            font-family: Lato;
        }

        li {
            margin: .5rem;
        }

        i {
            color: #aaa;
            position: absolute;
            top: .65rem;
            left: .5rem;
        }
    `}</style>
</>)
