import Link from 'next/link';

function ArticlePreview({ article, imageLeft }) {
    return (<>
        <div className="article-preview">
            {imageLeft && <img src={article.full_thumbnail} alt="thumbnail" />}
            <div className="article-preview-words">
                <div><Link href={`/articles/${article.id}`}><a>{article.title}</a></Link></div>
                <div>{new Date(article.publish_date).toLocaleDateString('default', { month: 'long', year: 'numeric', day: 'numeric' })} by <Link href={`/author/${article.author.id}`}><a>{article.author.name}</a></Link></div>
                <div>{article.description}</div>
                <Link href={`/articles/${article.id}`}><a className="read-more">read more</a></Link>
            </div>
            {!imageLeft && <img src={article.full_thumbnail} alt="thumbnail" />}
        </div>

        <style jsx>{`
            display: flex;

            .article-preview-words {
                display: flex;
                flex-direction: column;
            }

            .article-preview-title {
                color: black;
            }

            .article-preview-author {
                color: gray;
                font-style: italic;
            }

            a:hover {
                color: ${CSSVars.hover.link};
            }
        `}</style>
    </>)
}

export default ArticlePreview
