import pool from '../../db';
export default async function (req, res) {
  try {
    var client = await pool.connect(),
        {
      rows
    } = await client.query(req.body.query, req.body.params);
  } catch (e) {
    console.log(e);
  } finally {
    client.release();
  }

  res.json(rows);
}
