import Header from '../components/Header';
import Sidebar from '../components/Sidebar';

function Layout({ categories, children }) {
    return (<div onScrollCapture={e => { console.log(e.target.children[2].children.style.display); e.target.children[2].children[0].style.display = 'flex' }}>
        <div style={{ backgroundColor: '#444', color: '#fff', height: '8rem', display: 'flex', justifyContent: 'center', alignItems: 'center' }}><span>Ad Space</span></div>
        <div className="main">
            <Header categories={categories} />
            <div style={{ display: 'flex' }}>
                {children}
                <Sidebar />
            </div>
        </div>
        <div style={{ display: 'flex', justifyContent: 'center' }}>
            <div id="bottom-ad" style={{ backgroundColor: '#444', color: '#fff', height: '7rem', display: 'flex', justifyContent: 'center', alignItems: 'center', position: 'fixed', bottom: 0, width: '50rem', zIndex: 3, display: 'none' }}>
                <span>Ad Space</span>
                <button className="bottom-ad-button" onClick={e => { e.target.parentElement.parentElement.style.display = 'none' }}>x</button>
            </div>
        </div>

        <script>{
            window.addEventListener('scroll', _=> { console.log(document.getElementById('bottom-ad').style.display); document.getElementById('bottom-ad').style.display = 'flex' }, true)
        }</script>

        <style jsx>{`
           .main {
               margin: 0 1rem 1rem 1rem;
           }

           .bottom-ad-button {
               background-color: coral;
               cursor: pointer;
               border: 1px solid white;
               color: white;
               font-size: .75rem;
               padding: 0 .25rem;
               position: absolute;
               top: 5px;
               right: 5px;
           }
        `}</style>
    </div>)
}

export default Layout
