import Link from 'next/link';

function CategoryPreview({ category: { title, articles } }) {
    return (
        <div className="category-preview">
            <label>{title}</label>
            <ul>
                {/* three most recent articles in category, sort by publish_date*/}
                {articles.slice(0, 3).map((article, i) =>
                    <li key={i}>
                        <div style={{ fontWeight: 600 }}><Link href={`/articles/${article.id}`}><a className="article-preview-title">{article.title}</a></Link></div>
                        <div style={{ color: 'gray' }}>{article.publish_date} by <Link href={`/authors/${article.author.id}`} className="author-link"><a className="article-preview-author">{article.author.name}</a></Link></div>
                        <div style={{ display: 'flex' }}>
                            <img src={article.full_thumbnail} alt="thumbnail" />
                            <div style={{ display: 'flex', flexDirection: 'column' }}>
                                <span>{article.description}</span>
                                <Link href={`/articles/${article.id}`}><a style={{ color: '#0088CC'}}>read more</a></Link>
                            </div>
                        </div>
                    </li>
                )}
            </ul>
            <style jsx>{`
                ul {
                    display: flex;
                    flex-direction: column;
                }

                li {
                    margin: 1rem;
                }

                label {
                    position: relative;
                    font-size: 2rem;
                    padding: 4px;
                    width: 100%;
                    font-weight: 600;
                }

                label::before,
                label::after {
                    position: absolute;
                    content: '';
                    height: 2px;
                    width: 100%;
                    background-color: #F4C900;
                    left: 0;
                }

                label::after {
                    bottom: 0;
                }

                .article-preview-title {
                    color: black;
                }

                .article-preview-author {
                    color: gray;
                    font-style: italic;
                }

                a:hover {
                    color: #0088CC;
                }
            `}</style>
        </div>
    )
}

export default CategoryPreview
