import { queryDB } from '../../Functions';
import ArticleDisplay from '../../components/ArticleDisplay';

export async function getStaticPaths() {
    let titles = await queryDB("SELECT title FROM categories WHERE title <> 'Labyrinth'");
    let paths = titles.map(title => ({ params: { category: title.title.toLowerCase().replace(/ /g, '-') } }));

    return { paths, fallback: false }
}

export async function getStaticProps({ params }) {
    let title = params.category.split('-').map((word, i) => (i === 0 || !['the', 'on'].includes(word)) ? word[0].toUpperCase() + word.slice(1) : word).join(' '),
        articleIDs = await queryDB('SELECT articles FROM categories WHERE title = $1', [title]),
        articles = await queryDB('SELECT * FROM articles WHERE id = ANY($1)', [articleIDs[0].articles]);

    return {
        props: JSON.parse(JSON.stringify({
            heading: title,
            articles
        }))
    }
}

export default ({ heading, articles }) => <Layout> <ArticleDisplay type="category" heading={heading} articles={articles} /> </Layout>
