export async function queryDB(query, params=[]) {
    try {
        var client = new Client({ connectionString: process.env.DATABASE_URL }),
            { rows } = await client.query(query, params);
    } catch (e) { console.log(e) }
    finally { client && client.end() }
    return rows;
}
