import Headlines from '../components/Headlines';
import CategoryPreview from '../components/CategoryPreview';
import { getCategories, queryDB } from '../Functions';
import Layout from '../layouts';

function App({ categories, headlines }) {
    return (<>
        <Layout categories={categories}>
            <div className="home">
                <Headlines articles={headlines} />
                <ul className="home-category-previews">
                    {categories.map((category, i) =>
                        <li key={i}>
                            <CategoryPreview category={category} />
                        </li>
                    )}
                </ul>
            </div>
        </Layout>
        <style jsx global>{`
            .home-category-previews {
                display: grid;
                grid-template-columns: 1fr 1fr;
                row-gap: 4rem;
                column-gap: .5rem;
                margin: 3rem;
            }
        `}</style>
    </>)
}

export async function getStaticProps() {
    let headlineIDs = await queryDB("SELECT articles FROM categories WHERE title = 'Headlines'"),
        headlines = await queryDB("SELECT * FROM articles WHERE id = $1", [headlineIDs[0].articles]);

    return { props: JSON.parse(JSON.stringify({ categories: await getCategories(), headlines })) }
}

export default App
