      "publish_date": "2019-05-23T07:00:00.000Z",
      "full_thumbnail": "https://i0.wp.com/dailynexus.com/wp-content/uploads/2019/05/MFA-Year-1_Tamari-Dzotsenidze_ONLINE-7-1.jpg?resize=355%2C230&ssl=1",
      "mobile_thumbnail": "https://i0.wp.com/dailynexus.com/wp-content/uploads/2019/05/MFA-Year-1_Tamari-Dzotsenidze_ONLINE-7-1.jpg?resize=980%2C647&ssl=1",
