export function convertDate(date, alternate=false) {
    if (alternate) {
        let now = new Date(),
            secondsPast = (now.getTime() - date) / 1000;
        if (secondsPast < 60) {
            return parseInt(secondsPast) + ` second${parseInt(secondsPast / 60) > 1 ? 's' : ''}`;
        }
        if (secondsPast < 3600) {
            return parseInt(secondsPast / 60) + ` month${parseInt(secondsPast / 3600) > 1 ? 's' : ''}`;
        }
        if (secondsPast <= 86400) {
            return parseInt(secondsPast / 3600) + ` hour${parseInt(secondsPast / 3600) > 1 ? 's' : ''}`;
        }
        if (secondsPast > 86400) {
            day = date.getDate();
            month = date.toDateString().match(/ [a-zA-Z]*/)[0].replace(" ", "");
            year = date.getFullYear() == now.getFullYear() ? "" : " " + date.getFullYear();
            return day + " " + month + year;
        }
    }
    return new Date(date).toLocaleDateString('default', { day: 'numeric', month: 'long', year: 'numeric' })
}

export function convertToPath(string) {
    return string.toLowerCase().replace(/ /g, '-').replace(/[:'!@#$%^&*\(\)";{}\[\]\|~`<>\/\\?\.,]/g, '')
}

export function convertFromPath(path) {
    return path.split('-').map((word, i) => (i === 0 || !['the', 'on'].includes(word)) ? word[0].toUpperCase() + word.slice(1) : word).join(' ')
}
