import Nav from './Nav';
import Link from 'next/link';

function Header({ categories }) {
    return (<>
        <div className="header">
            <div className="full-logo"><Link href="/"><a>DAILY <img className="logo" src="/img/nexus-logo.png" alt="logo" /> NEXUS </a></Link></div>
            <div className="under-logo">
                <span>{new Date().toLocaleDateString('default', { weekday: 'long', month: 'long', year: 'numeric', day: 'numeric' })}</span>
                <div>UNIVERSITY OF CALIFORNIA, SANTA BARBARA</div>
                <span>
                    asdf
                    <Link href="facebook.com/dailynexus"><icon src="fa-facebook" /></Link>
                    <Link href="twitter.com/dailynexus"><icon src="fa-twitter" /></Link>
                    <Link href="instagram.com/dailynexus"><icon src="fa-instagram" /></Link>
                </span>
            </div>
            <Nav categories={categories} />
        </div>
        <style jsx>{`
            .header {
                margin-top: 2rem;
            }

            .logo {
                width: 5.5rem;
                height: 6.5rem;
                transform: translateY(-2rem);
            }

            .full-logo {
                text-align: center;
            }

            .full-logo a {
                font-size: 4.5rem;
                font-family: Times New Roman;
                color: black;
            }

            .under-logo {
                position: relative;
                text-align: center;
                font-size: .825rem;
                padding: 4px;
            }

            .under-logo span {
                position: absolute;
            }

            .under-logo span:nth-child(1) {
                left: 5px;
            }

            .under-logo span:nth-child(3) {
                right: 8px;
                top: 4px;
            }

            .under-logo::before,
            .under-logo::after {
                width: 100%;
                content: '';
                position: absolute;
                left: 0;
            }

            .under-logo::before {
                background-color: var(--theme-blue);
                height: 1px;
                top: 0;
            }

            .under-logo::after {
                height: 3px;
                bottom: 0;
                background-color: var(--theme-gold); 
            }
        `}</style>
    </>)
}

export default Header
