export default ({ comments }) => (<>
    <div className="comment-section">
        <div>
            <span>{comments.length}</span>
            <span>Leave a Reply</span>
        </div>
        <div style={{ position: 'relative', marginBottom: '2rem' }}>
            <div style={{ position: 'relative' }}>
                <img className="input-large-img" src="https://secure.gravatar.com/avatar/?s=40&d=mm&r=g" alt="user" />
                <input type="text" placeholder="Join the discussion..." />
            </div>
            <div style={{ position: 'relative' }}>
                <i className="fas fa-user input-icon"></i>
                <input className="input-small" type="text" placeholder="Name*" />
            </div>
            <div style={{ position: 'relative' }}><i className="fas fa-at input-icon"></i>
                <input className="input-small" type="text" placeholder="Email*" />
            </div>
        </div>
        <ul className="icons">
            <li key="0"><i className="fas fa-align-left" ></i></li>
            <li key="1"><i className="far fa-comments" ></i></li>
            <li key="2"><i className="fas fa-rss" ></i></li>
            <li key="3"><i className="fas fa-bolt" style={{ color: '#ffa600' }}></i></li>
            <li key="4"><i className="fab fa-hotjar" style={{ color: '#fc5844' }}></i></li>
        </ul>
        <ul className="comments">
            {comments.map((comment, i) => <li key={i}><Comment comment={comment} /></li>)}
        </ul>
    </div>

    <style jsx>{`
        .comment-section > div:nth-child(1) {
            position: relative;
            display: flex;
            margin-bottom: 4rem;
        }

        .comment-section > div:nth-child(1)::after {
            position: absolute;
            content: '';
            background-color: lightgray;
            width: 100%;
            bottom: -1.25rem;
            height: 1px;
            left: 0;
        }

        .comment-section > div:nth-child(1) > span {
            margin-right: .9rem;
            font-size: 1.3rem;
        }

        .comment-section > div:nth-child(1) > span:nth-child(1) {
            background-color: #666;
            padding: .325rem 1rem;
            font-size: 1rem;
            color: white;
        }

        input {
            padding: 1.7rem;
            padding-left: 5rem;
            border: 1px solid #ddd;
            color: #888;
            font-family: monospace;
            font-size: 1.15rem;
            width: 100%;
            margin-bottom: .6rem;
        }

        .input-large-img {
            position: absolute;
            top: 1rem;
            left: 1rem;
            box-shadow: 0 0 4px 2px #d8d8d8;
        }

        .input-small {
            font-size: .9rem;
            padding: .5rem 1.9rem;
            width: 50%;
            font-family: Lato;
        }

        li {
            margin: .5rem;
        }

        .input-icon {
            color: #aaa;
            position: absolute;
            top: .65rem;
            left: .5rem;
        }

        .comment-section > div:nth-child(2)::after {
            content: '';
            background-color: lightgray;
            width: 100%;
            height: 1px;
            position: absolute;
            bottom: -.95rem;
        }

        .icons {
            display: flex;
            position: relative;
            width: 9.5rem;
        }

        .icons li {
            margin: 0 .75rem;
            color: #666;
            font-size: 1.05rem;
        }

        .icons li:nth-child(1) i {
            transform: rotateZ(3.15rad);
        }
        
        .icons li::before {
            border-radius: 10px;
            background-color: #555;
            text-align: center;
            font-size: .85rem;
            padding: .25rem 0;
            width: 80%;
            left: 10%;
            top: -50%;
            color: white;
            position: absolute;
            display: none;
        }

        .icons li:hover::before {
            display: block;
        }

        .icons li:nth-child(1)::before { content: 'Comment threads'; }
        .icons li:nth-child(2)::before { content: 'Thread replies'; }
        .icons li:nth-child(3)before { content: 'Followers'; }
        .icons li:nth-child(4)before { content: 'Most reacted comment'; }
        .icons li:nth-child(5)before { content: 'Hottest comment thread'; }
    `}</style>
</>)
