import { queryDB, getCategories } from '../../../db';
import ArticleDisplay from '../../../components/ArticleDisplay';
import Layout from '../../../layouts';

export async function getStaticPaths() {
    let categories = await queryDB("SELECT title, subcategories FROM categories WHERE title <> 'Labyrinth' AND title <> 'Headlines'"),
        paths = categories
            .reduce((acc, category) => [
                ...acc,
                ...Object.keys(category.subcategories)
                    .map(subcategory => ({ params: { category: category.title, subcategory } }))
            ], []);

    return JSON.parse(JSON.stringify({ paths, fallback: false }))
}

export async function getStaticProps({ params: { category, subcategory } }) {
    let subcategories = await queryDB('SELECT subcategories FROM categories WHERE title = $1', [category]),
        articles = await queryDB('SELECT * FROM articles WHERE id = ANY($1)', [ subcategories[0].subcategories[subcategory] ]);

    return {
        props: JSON.parse(JSON.stringify({
            heading: subcategory,
            articles,
            categories: await getCategories()
        }))
    }
}

export default ({ heading, articles, categories }) => <Layout categories={categories}> <ArticleDisplay type={'subcategory'} heading={heading} articles={articles} /> </Layout>
