import Link from 'next/link';

const newsletterURL = 'https://dailynexus.us19.list-manage.com/subscribe?u=c7afb6c46dbbdcd7bb0bd8919&id=7220084bdc';

const twitterURLs = {
    main: 'https://twitter.com/dailynexus?ref_src=twsrc%5Etfw%7Ctwcamp%5Eembeddedtimeline%7Ctwterm%5Eprofile%3Adailynexus&ref_url=https%3A%2F%2Fdailynexus.com%2F',

    embed: 'https://publish.twitter.com/?url=https%3A%2F%2Ftwitter.com%2Fdailynexus&ref_src=twsrc%5Etfw%7Ctwcamp%5Eembeddedtimeline%7Ctwterm%5Eprofile%3Adailynexus&ref_url=https%3A%2F%2Fdailynexus.com%2F#'
};

const facebookURL = 'https://www.facebook.com/dailynexus/';

const archivesURL = 'https://dailynexus.com/archives/';

function Sidebar() {
    return (<>
        <ul className="sidebar">
            <li key="0"><div style={{
                color: 'white',
                backgroundColor: '#444',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                height: '14rem' }}>Ad Space</div></li>
            <li key="1">
                <img src="/img/covid-chart.png" alt="covid chart" /></li>
            <li key="2">
                <div className="comic-of-the-week-title">Comic of the Week</div>
                <hr />
                <br />
                <div className="comic-of-the-week"><img src="/img/comic-of-the-week.webp" alt="comic" /></div>
            </li>
            <li key="3"> <Link href={newsletterURL}><button className="newsletter-button">Subscribe to Our Newsletter</button></Link> </li>
            <li key="4">
                <div>Tweets by <Link href={twitterURLs.main}><a>@dailynexus</a></Link></div>
                <div>Interactive Twitter Preview</div>
                <div>
                    <Link href={twitterURLs.main}><a>Embed</a></Link>
                    <Link href={twitterURLs.embed}><a>View on Twitter</a></Link>
                </div>
            </li>
            <li key="5"> <Link href={facebookURL}><button className="facebook-button">Like us on Facebook</button></Link> </li>
            <li key="6"> <Link href={archivesURL}><img src="/img/print-edition.png" alt="archives" /></Link> </li>
            <li key="7">
                <div style={{ textAlign: 'center', fontWeight: 'bold', fontSize: '1.5rem', marginTop: '3rem', marginBottom: '.6rem' }}>WEEKLY HOROSCOPES</div>
                <div style={{ textAlign: 'center', color: '#333', fontSize: '1.25rem' }}>by <Link href="https://twitter.com/dailynexopinion"><a style={{ color: 'var(--link-hover)' }}>@dailynexopinion</a></Link></div>
                <br />
                <hr />
                <br />
                <div style={{ color: '#333', textAlign: 'center', fontSize: '1.3rem' }}>The Signs as Summer Desserts</div>
                <br />
                <hr />
                <br />
                <ul>
                    {[
                        {'Aries': ['March 21', 'April 19', 'Yerba mate chocolate truffles']},
                        {'Taurus': ['April 20 - May 20', 'triple chocolate cake']},
                        {'Gemini': ['May 21 - June 20', 'Fireball pumpkin pie']},
                        {'Cancer': ['June 21 - July 22', 'Bucket of ice water']},
                        {'Leo': ['July 23 - August 22', 'S\'mores']},
                        {'Virgo': ['August 23 - September 22', 'Vodka mocha bundt cake']},
                        {'Libra': ['September 23 - October 22', 'Chocolate-covered almonds']},
                        {'Scorpio': ['October 23 - November 21', 'Frozen yogurt with Hot Cheeto topping']},
                        {'Sagittarius': ['November 22 - December 21', 'Jar of mayonnaise']},
                        {'Capricorn': ['December 22 - January 19', 'Rum cake']},
                        {'Aquarius': ['January 20 - February 18', 'Oatmeal raisin cookies']},
                        {'Pisces': ['February 19 - March 20', 'Raspberry cheesecake']}
                    ].map((sign, i) => {
                        let [[name, [dates, horoscope]]] = Object.entries(sign);
                        return (
                            <li key={i}>
                                <img svg={`/img/horoscopes/${name.toLowerCase()}.svg`} alt="sign" />
                                <div>{name}</div>
                                <div>{dates}</div>
                                <div>{horoscope}</div>
                            </li>
                        )
                    })}
                </ul>
            </li>
        </ul>

        <style jsx>{`
            .comic-of-the-week-title {
                font-weight: bold;
                font-size: 1.5rem;
                text-align: center;
                margin-bottom: .5rem;
            }

            .comic-of-the-week img{
                object-fit: cover;
                width: 100%;
                height: 14rem;
                position: relative;
            }

            .comic-of-the-week {
                position: relative;
                height: 14rem;
            }

            .comic-of-the-week::before {
                position: absolute;
                top: 0;
                right: 0;
                bottom: 0;
                left: 0;
                content: 'College Nostalgia';
                font-weight: bold;
                font-size: 1.2rem;
                color: white;
                background-color: rgba(0, 0, 0, .5);
                display: flex;
                justify-content: center;
                align-items: center;
                z-index: 2;
            }

            .newsletter-button {
                background-color: var(--theme-gold);
                color: white;
                width: 92.5%;
                font-size: 1.25rem;
                border-radius: 5px;
                border: none;
                padding: .75rem;
                margin: 1rem .75rem;
                text-align: center;
            }

            .facebook-button {
                background-color: #cornflowerblue;
                width: 95%;
                fontSize: 1.25rem;
                color: white;
                text-align: center;
            }
        `}</style>
    </>)
}

export default Sidebar
