import Link from 'next/link';

function Nav({ categories }) {    
    return (<>
        <ul className="nav">
            {categories.sort((a, b) => ([a.title, b.title].sort()[0] === a.title) ? 0 : 1).map((category, i) =>
                <li key={i}>
                    <Link href={`/categories/${category.title.toLowerCase().replace(/ /g, '-')}`}><a onHover={e => {
                        e.target.parentElement.children[1].opacity = 1;
                        e.target.parentElement.children[1].pointerEvents = 'auto';
                    }}>{category.title}</a></Link>
                    <ul className="nav-subcategories">
                        {Object.keys(category.subcategories).map((subcategory, i) =>
                            <li key={i}>{subcategory}</li>)}
                    </ul>
                </li>)
            }
            <li><icon src="fa-search" /></li>
        </ul>

        <style jsx>{`
            .nav {
                display: flex;
                justify-content: space-evenly;
                align-items: center;
                height: 3rem;
                text-transform: uppercase;
                font-weight: 600;
            }

            a {
                color: black;
            }

            a:hover {
                color: var(--link-hover);
            }

            .nav-subcategories {
                opacity: 0;
                pointer-events: none;
                transition: transform 250ms ease-in;
            }
        `}</style>
    </>)
}

export default Nav
