import '../styles/style.css';
import Layout from '../layouts';

export async function getStaticProps() {
    let categories = await queryDB("SELECT * FROM categories WHERE title <> 'Headlines' AND title <> 'Labyrinth'");

    for (let category of categories) {
        for (let id of category.articles) {
            let hydratedArticle = await queryDB("SELECT * FROM articles WHERE id = $1", [id]);
            category.articles.splice(category.articles.indexOf(id), 1, hydratedArticle[0]);
        }
    }

    return { props: { categories } };
}

export default ({ Component, pageProps, categories }) => <Layout categories={categories}> <Component {...pageProps} /> </Layout>
