function Comment({ comment: { picture, name, email, content, post_date } }, topLevel, moreBelow) {
    return (<>
        <div className="comment">
            <img src={picture} alt="user" />
            <div>
                <div style={{ display: 'flex' }}>
                    <span>{name}</span>
                    <i className="fas fa-link"></i>
                </div>
                <div>{content}</div>
                <div style={{ display: 'flex' }}>
                    <button>{topLevel ? <i className="far fa-comments"></i> : ''} Reply</button>
                    <span>
                        <span>
                            <i className="far fa-clock"></i>
                            {post_date}
                        </span>
                        {moreBelow ? <i className="fas fa-chevron-up"></i> : ''}
                    </span>
                </div>
            </div>
        </div>

        <style jsx>{`
            .comment > div {
                background-color: #f7f7f7;
                padding: .5rem;
                margin-left: .5rem;
            }

            .comment > div > div:nth-child(1) span {
                color: turquoise;
            }

            .comment > div >div:nth-child(2) {
                margin-top: 2.5rem;
                margin-bottom: 1rem;
            }

            button {
                color: aqua;
                border: 1px solid aqua;
                padding: .4rem;
            }
        `}</style>
    </>)
}

export default Comment
