export function convertDate(date) {
    return new Date(date).toLocaleDateString('default', { day: 'numeric', month: 'long', year: 'numeric' })
}

export function convertToPath(string) {
    return string.toLowerCase().replace(/ /g, '-').replace(/:'"/g, '');
}
