import { queryDB } from '../../../db';
import ArticlePreview from '../../../components/ArticlePreview';
import Link from 'next/link';

export async function getStaticProps() {
    let [articleIDs] = await queryDB("SELECT articles FROM categories WHERE title = 'Headlines'"),
        articles = await queryDB('SELECT * FROM articles WHERE id = ANY($1)', [articleIDs.articles]);

    return {
        props: JSON.parse(JSON.stringify({ articles }))
    }
}

export default ({ articles }) => (<>
    <img src="https://dailynexus.com/wp-content/themes/dailynexus/graphics/labyrinthmasthead.png" alt="labyrinth" style={{ width: '100%', height: '20rem', objectFit: 'contain' }} />
    <div style={{ display: 'flex', justifyContent: 'space-between', padding: '1.25rem', backgroundColor: 'beige' }}>
        <span>
            <Link href="/"><a style={{ color: 'black', cursor: 'pointer' }}>Home</a></Link>
        </span>
        <span>
            <Link href="https://dailynexus.com/2018-04-25/resolutions-on-the-table/"><a style={{ color: 'black', cursor: 'pointer' }}>A.S. Senate Bill Tracker</a></Link>
            <Link href="/about"><a style={{ color: 'black', marginLeft: '3rem', cursor: 'pointer' }}>About</a></Link>
        </span>
    </div>
    <ul style={{ marginLeft: '15rem', width: '70%' }}>
        {articles.slice(0, 11).map((article, i) =>
            <li key={i}><ArticlePreview article={article} imageLeft /></li>
        )}
    </ul>
</>)
