import Link from 'next/link';

function ArticlePreview({ article, imageLeft }) {
    return (<>
        <div className="article-preview">
            {imageLeft && <img src={article.full_thumbnail} alt="thumbnail" />}
            <div className="article-preview-words">
                <div><Link href={`/articles/${article.id}`}><a>{article.title}</a></Link></div>
                <div>{article.publish_date} by <Link href={`/author/${article.author.id}`} className="author-link"><a>{article.author.name}</a></Link></div>
                <div>{article.description}</div>
                <Link href={`/articles/${article.id}`}><a>read more</a></Link>
            </div>
            {!imageLeft && <img src={article.full_thumbnail} alt="thumbnail" />}
        </div>

        <style jsx>{`
            display: flex;
        `}</style>
    </>)
}

export default ArticlePreview
