import Link from 'next/link';
import { convertToPath } from '../Functions';

function Nav({ categories }) {
    const timeouts = [];
    
    return (<>
        <ul className="nav">
            {categories.sort((a, b) => ([a.title, b.title].sort()[0] === a.title) ? 0 : 1).map((category, i) =>
                <li key={i}>
                    <Link href={`/categories/${category.title.toLowerCase().replace(/ /g, '-')}`}><a onMouseOver={e => {
                        timeouts[i] && clearTimeout(timeouts[i]);
                        let { style } = e.target.parentElement.children[1];
                        style.opacity = 1;
                        style.pointerEvents = 'auto';
                    }} onMouseOut={e => {
                        let { style } = e.target.parentElement.children[1];
                        timeouts[i] = setTimeout(_=> {
                            style.opacity = 0;
                            style.pointerEvents = 'none';
                        }, 0);
                    }}>{category.title}</a></Link>
                    <ul className="nav-subcategories" onMouseMove={_=> timeouts[i] && clearTimeout(timeouts[i])} onMouseOut={e => {
                        let { style } = e.target;
                        timeouts[i] = setTimeout(_=> {
                            style.opacity = 0;
                            style.pointerEvents = 'none';
                        }, 0);
                    }}>
                        {Object.keys(category.subcategories).map((subcategory, j) =>
                            <li key={j}><Link href={`/categories/${convertToPath(category.title)}/${convertToPath(subcategory)}`}><a className="subcategory-link">{subcategory}</a></Link></li>)}
                    </ul>
                </li>)
            }
            <script src="https://kit.fontawesome.com/7cab195da1.js" crossorigin="anonymous"></script>
            <li style={{ position: 'relative' }}>
                <i className="search fas fa-search" onClick={e => {
                    let { style } = e.target.parentElement.children[1];
                    style.display = (style.display === 'none') ? 'block' : 'none'
                }}></i>
                <input type="text" placeholder="Looking for something?" style={{ display: 'none', position: 'absolute', right: '150%', top: '-15%', padding: '.2rem .4rem' }}/>
            </li>
        </ul>

        <style jsx>{`
            .nav {
                display: flex;
                justify-content: space-evenly;
                align-items: center;
                height: 3rem;
                text-transform: uppercase;
                font-weight: 600;
            }

            .nav li {
                position: relative;
            }

            .nav li:hover ul {
                max-height: 1000px;
            }

            a {
                color: black;
            }

            a:hover {
                color: var(--link-hover);
            }

            .nav-subcategories {
                padding: 1.25rem .6rem .5rem .6rem;
                font-weight: 300;
                background-color: #fff;
                opacity: 0;
                pointer-events: none;
                position: absolute;
                top: 85%;
                transition: opacity 250ms ease-in;
                font-size: 15px;
                z-index: 5;
            }

            .nav-subcategories li {
                margin-top: 7px;
                cursor: pointer;
            }

            .nav-subcategories li:nth-child(1) {
                margin-top: 0;
            }

            .nav-subcategories li:hover {
                color: var(--link-hover);
            }

            .subcategory-link {
                color: black;
            }

            .subcategory-link:hover {
                color: var(--link-hover);
            }

            .search {
                cursor: pointer;
            }
        `}</style>
    </>)
}

export default Nav
