import Headlines from '../components/Headlines';
import CategoryPreview from '../components/CategoryPreview';
import { queryDB } from '../Functions';
import Layout from '../layouts';
import './style.css';

function App({ categories, headlines }) {
    return (
        <Layout categories={categories}>
            <div className="home">
                {/* <Headlines articles={headlines} /> */}
                <ul className="home-category-previews">
                    {categories.map((category, i) =>
                        <li key={i}>
                            <CategoryPreview category={category} />
                        </li>
                    )}
                </ul>
            </div>
        </Layout>
    )
}

export async function getStaticProps() {
    let categories = await queryDB("SELECT * FROM categories WHERE title <> 'Headlines' AND title <> 'Labyrinth'"),
        headlines = await queryDB("SELECT * FROM categories WHERE title = 'Headlines'");
    
    for (let category of categories) {
        for (let id of category.articles) {
            let hydratedArticle = await queryDB("SELECT * FROM articles WHERE id = $1", [id]);
            category.articles.splice(category.articles.indexOf(id), 1, hydratedArticle[0]);
        }
    }

    return { props: JSON.parse(JSON.stringify({ categories, headlines })) }
}

export default App
