export default ({ comments }) => (<>
    <div className="comment-section">
        <input type="text" placeholder="Join the discussion..." />
        <ul style={{ display: 'flex' }}>
            <li key="0"><i className="fas fa-align-left"></i></li>
            <li key="1"><i className="far fa-comments"></i></li>
            <li key="2"><i className="fas fa-rss"></i></li>
            <li key="3"><i className="fas fa-bolt"></i></li>
            <li key="4"><i className="fab fa-hotjar"></i></li>
        </ul>
        <ul className="comments">
            {comments.map((comment, i) => <li key={i}><Comment comment={comment} /></li>)}
        </ul>
    </div>

    <style jsx>{`
        input {
            padding: 1.5rem;
            padding-left: 5rem;
            border: 1px solid #ccc;
            color: #aaa;
        }

        input::before {
            content: '';
            background-image: url('https://secure.gravatar.com/avatar/?s=40&d=mm&r=g');
            position: absolute;
            left: 1rem;
            top: 1rem;
        }

        li {
            margin: .5rem;
        }
    `}</style>
</>)
