import pool from '../../db';
export default async function (req, res) {
  console.log('in querydb');
  const {
    query,
    params
  } = req.body;

  try {
    var client = await pool.connect();
    console.log('pool connected');
    var {
      rows
    } = await client.query(query, params);
    console.log('rows retrieved');
  } catch (e) {
    console.log(e);
  } finally {
    client.release();
  }

  res.json(rows);
}
