import Link from 'next/link';
import AboutTheAuthor from '../../components/AboutTheAuthor';
import Related from '../../components/Related';
import CommentSection from '../../components/CommentSection';
import { queryDB, getCategories } from '../../db';
import { convertDate } from '../../Functions';
import Layout from '../../layouts';
import { useEffect } from 'react';

function Article({ article, author, related, categories }) {

    useEffect(_=> {
        [...document.getElementsByTagName('p')].forEach(p => {
            p.style.margin = '1rem 0';
            p.style.fontFamily = 'Times New Roman';
        });
        [document.getElementById('jp-relatedposts'), document.getElementsByClassName('saboxplugin-wrap')[0]].forEach(el => { el.style.display = 'none' });
        document.getElementsByClassName('single-post-content')[0].style.fontFamily = 'Times New Roman';
        [...document.getElementsByClassName('gallery-row')].forEach(gallery => {
            gallery.style.display = 'grid';
            gallery.style.gridTemplateColumns = 'repeat(3, 1fr)';
        });
        [...document.getElementsByClassName('tiled-gallery-caption')].forEach(caption => {
            caption.style.backgroundColor = 'rgb(200, 200, 200, .9)';
            caption.style.maxHeight = 0;
            caption.style.transition = 'max-height 150 ease-out';
            caption.style.position = 'absolute';
            caption.style.bottom = 0;
        });
        [...document.getElementsByClassName('tiled-gallery-item')].forEach(item => {
            item.style.position = 'relative';
            item.addEventListener('mouseover', e => {
            e.currentTarget.children[1].maxHeight = '1.75rem';
            });
            item.addEventListener('mouseout', e => {
                e.currentTarget.children[1].maxHeight = 0;
            });
        });
    }, []);

    return (<>
        <Layout categories={categories}>
            <div className="article">
                <div className="article-page-subcategory">{article.subcategory}</div>
                <div className="article-page-details">
                    <div className="article-page-title">{article.title}</div>
                    <div>{convertDate(article.publish_date)} by <Link href={`/authors/${author.id}`}><a className="article-page-author-name">{author.name}</a></Link></div>
                </div>
                <div className="article-page-content" dangerouslySetInnerHTML={{ __html: article.content }} />
                {author.name && (author.biography || author.portrait) && <AboutTheAuthor author={author} />}
                <Related articles={related} />
                <CommentSection />
            </div>
        </Layout>

        <style jsx>{`
            .article {
                margin: .15rem 1rem;
            }

            .article-page-subcategory {
                position: relative;
                font-weight: bold;
                font-size: 1.2rem;
                text-transform: uppercase;
            }

            .article-page-subcategory::before,
            .article-page-subcategory::after {
                content: '';
                height: 2px;
                width: 100%;
                position: absolute;
                background-color: #ddd;
                left: 0;
            }

            .article-page-subcategory::before {
                top: -5px;
            }

            .article-page-subcategory::after {
                bottom: -5px;
            }

            .article-page-title {
                font-size: 2.5rem;
                font-weight: 600;
                font-family: Times New Roman;
                margin-bottom: .65rem;
            }

            
            .article-page-author-name {
                color: black;
            }

            .article-page-author-name:hover {
                color: var(--link-hover);
            }

            .article-page-details {
                margin: 2.5rem 0 3.5rem 0;
            }

            .article-page-content {
                margin: 0 .5rem;
            }
        `}</style>
    </>)
}

export async function getStaticPaths() {
    let ids = await queryDB('SELECT id FROM articles'),
        paths = ids.map(id => ({ params: { id: String(id.id) } }));

    return { paths, fallback: false }
}

export async function getStaticProps({ params: { id }}) {
    let [article] = await queryDB('SELECT * FROM articles WHERE id = $1', [id]),
        related = await queryDB('SELECT * FROM articles WHERE id = ANY($1)', [article.related]),
        [author] = await queryDB('SELECT * FROM authors WHERE name = $1', [article.author.name]);

    return { props: JSON.parse(JSON.stringify({ article, author, related, categories: await getCategories() })) }
}

export default Article;
